mapper.select:
SELECT MYNO,MYID,MYPW,MYNAME,MYADDR,MYPHONE,MYEMAIL,MYENABLED,MYROLE 
FROM LOGINBOARD 
WHERE MYID = #{myid} AND MYPW = #{mypw} AND MYENABLED = #{myenabled}

SELECT MYID 
FROM LOGINBOARD 
WHERE MYID = #{myid}

mapper.update:
UPDATE LOGINBOARD 
SET MYADDR = #{myaddr}, MYPW = #{mypw}, MYPHONE = #{myphone}, MYEMAIL = #{myemail} 
WHERE MYNO = #{myno}"

UPDATE LOGINBOARD 
SET MYENABLED = 'N' 
WHERE MYNO = #{myno}

UPDATE MDBOARD
SET WRITER = #{writer}, TITLE = #{title}, CONTENT=#{content}
WHERE SEQ = #{seq}

mapper.delete:
DELETE FROM MDBOARD
WHERE SEQ IN

#{seq}
